import styled from 'styled-components';

export const Container = styled.header`
    height: 46px;
    background: #7159c1;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.25);
`;



export const Content = styled.section`
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 64px;
  nav {
    display: flex;
    align-items: center;

    img {
      margin-right: 20px;
      padding-right: 20px;
      border-right: 1px solid #eee;
    }
    a {
      font-weight: bold;
      color: #7159c1;
    }
  }
  aside {
    display: flex;
    align-items: center;
  }
`;
