import styled from 'styled-components';

export const Container = styled.div`
      font-size: 18px;
      line-height: 21px;
      width: 600px;
      height: 50px;
      margin-top: 33px;
      border-radius: 20px;
      border: 1px solid #E6ECF0;
      color: #9B9B9B;
      padding: 0 12px;
      opacity: 0.7;
      background: rgba(254, 254, 254, 0.7);

      display: flex;
      align-items: center;
      input {
        background: transparent;
        flex: 1;
      }`;
