import styled from 'styled-components';

export const Title = styled.h1`
    font-size: 30px;
    color: red;
`;

export const Image = styled.img`
    height: 100%;
    width: 100%;
`;

export const BannerPrincipal = styled.div`
  padding: 0 30px;
  div {
    text-align: right;
    margin-right: 60px;
    strong {
      display: block;
      color: #333;
    }
    a {
      display: block;
      margin-top: 2px;
      font-size: 16px;
      color: #fff;
      text-decoration: none;
      font-weight: 700;
    }
  }
  img {
    width: 100%;
    height: 100%;
    border-radius: 32px;
  }
`;
