import styled from 'styled-components';

export const Container = styled.div`
  width: 100%;
  max-width: 1120px;
  height: 100vh;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const Form = styled.form`
  width: 100%;
  max-width: 350px;
  margin-right: 30px;
    input {
      width: 339px;
      height: 31px;
      margin-top: 5px;
      border-radius: 50%;
    }
`;
