import styled from 'styled-components';

import BackGroundLogin from '../../assets/background-login.svg';


export const Container = styled.div`
  width: 100vw;
  height: 100vh;
  position: fixed;
  display: flex;
  align-items: center;
  justify-content: center;
  background: url(${BackGroundLogin}) no-repeat center;
`;

export const Form = styled.form`
  width: 100%;
  max-width: 350px;
  margin-right: 30px;
  flex-direction: column;
  display: flex;
    input {
      width: 339px;
      height: 31px;
      margin-top: 5px;
      border-radius: 20px;
      border: 1px solid #E6ECF0;
      padding: 0 12px;
    }
    button {
      height: 34px;
      background: #445CAE;
      display: flex;
      margin: 0 auto;
      margin-top: 30px;
      align-items: center;
      justify-content: center;
      width: 90px;
      font-weight: bold;
      font-size: 14px;
      color: #fff;
      border-radius: 16px;
      border: 0;
    }

    img {
      margin: 50px;
      height: 142px;
      width: 500px;
    }
`;
