import styled from 'styled-components';

export const Container = styled.div`
  width: 100%;
  max-width: 1120px;
  height: 100vh;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const Form = styled.form`
  width: 100%;
  max-width: 350px;
  margin-right: 30px;
    input {
      width: 339px;
      height: 31px;
      margin-top: 5px;
      border-radius: 20px;
      border: 1px solid #E6ECF0;
    }
    button {
      height: 34px;
      background: #3BB9E3;
      display: flex;
      margin: 0 auto;
      margin-top: 30px;
      align-items: center;
      justify-content: center;
      width: 90px;
      font-weight: bold;
      font-size: 14px;
      color: #fff;
      border-radius: 16px;
      border: 0;
    }
`;
