import React, { useCallback, useRef } from 'react';

import { Container,  Formulario} from './styles';
import { Form } from '@unform/web';
import { FormHandles } from '@unform/core';
import * as Yup from 'yup';
import getValidationErrors from '../../utils/getValidationErrors';





import LeftMenu from '../../components/LeftMenu'
import Input from '../../components/Input';
// import Button from '../../components/Button';

const Profile: React.FC = () => {


  const formRef = useRef<FormHandles>(null);

  const handleSubmit = useCallback(async (data : object) => {
    try {

      formRef.current?.setErrors({});
      const schema = Yup.object().shape({
        email: Yup.string().required('E-Mail Obrigatório').email('Digite um E-mail válido'),
        password: Yup.string().required('Senha obrigatória')
      });
      await schema.validate(data, {
        abortEarly: false,
      });  



    } catch (err) {
      console.log(err);

      const errors = getValidationErrors(err);
      formRef.current?.setErrors(errors);
    }
  }, []);


  return (
    <>
        <Container>
            <LeftMenu />
            <Formulario>
                <Form ref={formRef} onSubmit={handleSubmit}>
                  <strong>Nome</strong><Input name="email" />
                  <strong>E-mail</strong><Input name="email" />
                  <strong>Senha</strong><Input name="email" />
                  <strong>Confirmação da Senha</strong><Input name="email" />
                  <strong>Rua</strong><Input name="email" />
                  <strong>Número</strong><Input name="email" />
                  <strong>Bairro</strong><Input name="email" />
                  <strong>CEP</strong><Input name="email" />
                </Form>
            </Formulario>  

        </Container>
    </>
  );
};

export default Profile;
