import styled from 'styled-components';

export const Title = styled.h1`
    font-size: 30px;
    color: red;
`;

export const Image = styled.img`
    height: 100%;
    width: 100%;
`;

export const BannerPrincipal = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  div {
    strong {
      text-align: center;
      font-size: 30px;
      color: #fff;
    }
  }
  img {
    width: 100%;
  }
`;
