import styled from 'styled-components';

export const Title = styled.h1`
    font-size: 30px;
    color: red;
`;

export const Image = styled.img`
    height: 100%;
    width: 100%;
`;

export const Banner = styled.div`
    height: 380px;
    width: 100%;
    background-image: url("./assets/phone.png");
    display: flex;
    align-items: center;
    justify-content: center;
    color: #fff;
`;
