import styled from 'styled-components';

export const Container = styled.header`
  height: 51px;
  background: rgba(126, 49, 186, 0.8);
  fill: solid;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.25);
`;

export const Content = styled.section`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  padding: 0 15px;
  height: 100%;
  nav {
    display: flex;

    ul {
      display: flex;
      list-style: none;
    }

    li {
      display: flex;
      align-items: center;
      margin-left: 30px;

      font-size: 16px;
      color: #fff;
      font-weight: bold;
    }
  }
`;
